import React from 'react';
import Info from './components/info';
import Form from './components/form';
import Weather from './components/weather';

const API_KEY = '88ec93c8bc578fb7e09367b86bce7577';

class App extends React.Component {

  gettingWeather = async (event) => {
    event.preventDefault(); // убираем стандартное поведение, без перезагрузки
    const city = event.target.elements.city.value;
    const api_url = await 
    fetch(`https://samples.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}`); // прочитать адрес и получить информацию из него
    const data = await api_url.json();
    console.log(data);
  }

  render () {
    return(
      <div>
          <Info />
          <Form weatherMethod={this.gettingWeather}/>
          <Weather />
      </div>
    )
  }
}

export default App;
